    return (
      <div className="App">
        <h1>Zoltar Speaks</h1>
        {/* <Landing /> */}
        <img src="" alt="" />
        <form action="submit">
          <label htmlFor="">What is Your Wish?</label>
          <input type="text" onChange={this.handleChange} />
          <button type="submit" onClick={this.handleSubmit}>
            Deposit a Coin
